mapper.insert:
INSERT INTO auctionRecord (
    userId, auctionId, auctionTime, auctionPrice
) VALUES (
    #{userId}, #{auctionId}, #{auctionTime}, #{auctionPrice}
)

mapper.update:
UPDATE auctionRecord
userId = #{userId}, auctionId = #{auctionId}, auctionTime = #{auctionTime}, auctionPrice = #{auctionPrice},
WHERE id = #{id}
